import React, { Component } from 'react';
import axios from 'axios'
//import './css/style.css'
//import '../App.css'

class Login extends Component{
    constructor(props){
        super(props)
        this.state = {
            email: "",
            password: "",
        }
    }

changePassword = (e)=> {
    this.setState({
        name: e.target.value
    }); //semicolon reqd
}

changeEmail = (e)=> {
    this.setState({
        email: e.target.value
    }); 
}

 submitForm = ()=> {
    axios.defaults.withCredentials = true //since no ssl..
    axios.post('http://localhost:8080/login', {
        data : {
            email:this.state.email,
            password:this.state.password,
        }
     });

 }

render(){
    return(
        
         <form className='form'>
            <label className='font-serif font-bold'>Email: </label>
            <input className='input' type="text" placeholder="Email" onChange={this.changeEmail}></input>
            <br/>
            <label className='font-serif font-bold'>Password: </label>
            <input className='input'  type="Text" placeholder="Password" onChange={this.changePassword}></input>
            <br/>
            <button className='btn' onClick={this.submitForm}>SUBMIT</button> 
            <br/>
            <label className='font-serif font-bold'>Don't have an account? </label>
            <button className='btn' onClick={this.submitForm}>Sign-Up</button> 
            
        </form>
        
    )
}
}
export default Login;
