import React, { Component } from 'react';
//import axios from 'axios'
//import './css/style.css'
//import '../App.css'

class Login extends Component{
    constructor(props){
        super(props)
        this.state = {
            email: "",
            password: "",
        }
    }

changePassword = (e)=> {
    this.setState({
        name: e.target.value
    }); //semicolon reqd
}

changeEmail = (e)=> {
    this.setState({
        email: e.target.value
    }); 
}

//  submitForm = ()=> {
//     axios.defaults.withCredentials = true //since no ssl..
//     axios.post('http://localhost:8080/login', {
//         data : {
//             email:this.state.email,
//             password:this.state.password,
//         }
//      });

 //}

render(){
    return(
        <div className='form'>
            <form className='flex flex-col w-80' >
            <label className='background-color-blue p-2'>Email: </label>
            <input className='rounded-lg' type="text" placeholder="Email" onChange={this.changeEmail}></input>
            <br/>
            <label className='background-color-blue p-2'>Password: </label>
            <input className='rounded-lg'  type="Text" placeholder="Password" onChange={this.changePassword}></input>
            <br/>
            <button className='background-color-blue p-2 font-sm' onClick={this.submitForm}>Login</button> 
            <br/>
            <label className='text-white p-2 font-sm' w-2>Don't have an account? </label>
            <br/>
            <button className='background-color-blue p-2 font-sm'>Sign-Up</button> 
        </form>
        </div>

        
         
        
    );
}
}
export default Login;
